'use strict';/** * @ngdoc function * @name d8intranetApp.controller:MainCtrl * @description * # MainCtrl * Controller of the d8intranetApp */angular.module('d8intranetApp')    .controller('documentsController', function ($scope, $http, $rootScope, config, getDocs) {      $rootScope.dataLoaded = true;      getDocs.getDocuments(config.documentsListUrl).then(function (data) {        $scope.documents = data;      });    })    .controller('singleDocController', function ($scope, $routeParams, $rootScope, config, getDocs) {      $scope.nid = $routeParams.nid;      getDocs.getDocuments(config.documentsListUrl).then(function (data) {        $scope.documents = data;        $rootScope.dataLoaded = true;        angular.forEach($scope.documents, function (singleDoc) {          if (singleDoc.nid == $routeParams.nid) {            $scope.doc = singleDoc;          }        })      });    });